import { useState } from "react";

function WordInput (){
    const [word, setWord] = useState("");
    const handleChange = (event)=>{
        setWord(event.target.value);
    }
    return(
        <div className="d-flex mb-3 mt-3">
            <input type="text"
            className="form-control me-3" placeholder="Type in a single word ex: developer " value={word} onChange = {handleChange}/>   
            <button className="btn text-nowrap text-white" style={{backgroundColor : "blueviolet"}}>Process Word</button>
            <label>{word.length}</label>
        </div>
    )
}
export default WordInput;
