import { useState } from "react";

function App () {

    const [isPara, setIsPara] = useState(false);

    const handleClick = () => {
        setIsPara(!isPara);
    }
    return (
       <div className="container">
            <h1 className="text-bg">Text Analyzer</h1>
            <h3 className="text-secondary">Text Analyzer is a simple free online tool for SEO web content analysis that helps you find most frequent phrases and words, number of characters, words, sentences and paragraphs, and estimated read and speak time of your content.</h3>
            <div className="row border rounded-3 p-1 w-50 text-center" onClick={handleClick}>
                <div className="col w-50" >Word Input</div>
                <div className="col w-50">Paragraph</div>
            </div>
       </div>
    )
}

export default App;
