import { useState } from "react";

function WordInput (){
    const [word, setWord] = useState("");
    const handleChange = (event)=>{
        setWord(event.target.value);
    }

    const noOfWords = word.split(" ").filter(item => item != '').length;
    return(
       <>
            <div className="d-flex mb-3 mt-3">
                <input type="text" className="form-control me-3" placeholder="Type in a single word ex: developer " value={word} onChange = {handleChange}/>   
                <button className="btn text-nowrap text-white" style={{backgroundColor : "blueviolet"}}>Process Word</button>
            </div>
            <div className="w-50">
                <table className="table table-bordered">
                    <tbody>
                        <tr>
                            <td scope="row">Characters</td>
                            <td>Words</td>
                        </tr>
                    </tbody>
                    <tbody>
                        <tr>
                            <td scope="row">{word.length}</td>
                            <td>{noOfWords}</td>
                        </tr>
                    </tbody>
                </table>
            </div>

       </>
    )
}
export default WordInput;
