import { useState } from "react";
import DataTable from "./DataTable";

function Paragraph (){
    const [word, setWord] = useState("");

    const noOfWords = (words) => words.split(/[ .:;?!~,`"&|()<>{}[\]\r\n/\\]+/ ).filter(item => {
        item = item.replace(/[^\w\s']|_/g, '')
        return item !== ''; 
    });
    const config = [
        {
            label: 'Characters',
            render: (word) => word.length
        },
        {
            label: 'Words',
            render: (word) => noOfWords(word).length
        },
        {
            label: 'Characters',
            render: (word) => word.length
        },
        {
            label: 'Characters',
            render: (word) => word.length
        },
        {
            label: 'Characters',
            render: (word) => word.length
        },
    ];
    return(
        <div class="mb-3">
          <label for="" class="form-label"></label>
          <textarea class="form-control" name="" id="" rows="10 "></textarea>
          <DataTable word={word} config={config}/>
        </div>
    )
}
export default Paragraph;
